const xhr=new XMLHttpRequest,URL="https://reqres.in/api",userTemplate=document.getElementById("user").innerHTML,cardList=document.getElementById("cards"),prev=document.getElementById("prev"),next=document.getElementById("next"),newUser=document.getElementById("create"),getUsers=(e=1)=>(xhr.open("GET",URL+"/users?page="+e,!1),xhr.send(),JSON.parse(xhr.response)),addUserCardList=(e,t)=>{let r="";e.map(e=>({...e,name:e.first_name+" "+e.last_name})).forEach(e=>{r+=t.replaceAll("{{name}}",e.name).replaceAll("{{email}}",e.email).replaceAll("{{avatar}}",e.avatar)}),cardList.innerHTML=r};(()=>{let t=1;var e=getUsers(t)["data"];addUserCardList(e,userTemplate),next.addEventListener("click",()=>{var e=getUsers(++t)["data"];addUserCardList(e,userTemplate)}),prev.addEventListener("click",()=>{var e=getUsers(--t)["data"];addUserCardList(e,userTemplate)}),newUser.addEventListener("click",()=>{createUser()})})();